    override func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell {
        let cell = tableView.dequeueReusableCell(withIdentifier: "cell")

        cell?.textLabel?.text = searchCounty[indexPath.row]
        return cell!
    }
    func searchBar(_ searchBar: UISearchBar, textDidChange searchText: String) {

        searchCounty = []
        word = searchText
        print("Word :\(word)")
//        print("Name Name ListUser og:\(ListUsers)")
        if searchText == "" {
            searchCounty = countryNameArr
        }else{
            for i in countryNameArr{
                if i.lowercased().contains(searchText.lowercased()){
                    searchCounty.append(i)
                }
            }
        }
        
        
        
        self.tableView.reloadData()
    }
